import React, { createContext, useContext, useState } from 'react';

export interface Card {
  id: number;
  name: string;
  image: string;
  audio: string;
}

type CardSetType = 'set1' | 'set2' | 'set3';

interface CardContextProps {
  cardData: Card[];
  setCardSet: (type: CardSetType) => void;
  currentArray: number[];
  handleTempArr: (card: Card) => void;
  checkOrder: (array: number[]) => void;
  getCardNumber: (cardId: number) => number | null;
}

const CardContext = createContext<CardContextProps | undefined>(undefined);

export const useCardContext = (): CardContextProps => {
  const context = useContext(CardContext);
  if (!context) {
    throw new Error('useCardContext must be used within a CardProvider');
  }
  return context;
};

const cardSet1: Card[] = [
  { id: 1, name: 'Fallen Object', image: '/projects/blackwood-mansion/fall_object.webp',  audio: '../../asset/audio/bones-scream.mp3'},
  { id: 2, name: 'Suspicious Whispers', image: '/projects/blackwood-mansion/whisper_image.webp',  audio: '../../asset/audio/fart.mp3'},
  { id: 3, name: 'Bloody Knife', image: '/projects/blackwood-mansion/bloody_knife.png',  audio: '../../asset/audio/fart.mp3'},
  { id: 4, name: 'Torn Curtain', image: '/projects/blackwood-mansion/torn_curtain.png',  audio: '../../asset/audio/fart.mp3'},
  { id: 5, name: 'Locked Diary', image: '/projects/blackwood-mansion/locked_diary.png',  audio: '../../asset/audio/fart.mp3'},
];

const cardSet2: Card[] = [
  { id: 6, name: 'Ghost', image: '/projects/blackwood-mansion/ghost.png',  audio: '../../asset/audio/ghost.mp3'},
  { id: 7, name: 'Broken Mirror', image: '/projects/blackwood-mansion/mirror.png',  audio: '../../asset/audio/glass.mp3'},
  { id: 8, name: 'Creepy Shadow', image: '/projects/blackwood-mansion/shadow.png',  audio: '../../asset/audio/shadow.mp3'},
  { id: 9, name: 'Scary Clown', image: '/projects/blackwood-mansion/clown.png', audio: '../../asset/audio/clown.mp3'},
  { id: 10, name: 'Haunted Doll', image:'/projects/blackwood-mansion/doll.png', audio: '../../asset/audio/doll.mp3'},
];

const cardSet3: Card[] = [
  { id: 11, name: 'Graveyard', image: '/projects/blackwood-mansion/grave.png', audio: '../../asset/audio/grave.mp3'},
  { id: 12, name: 'Flying Bat', image: '/projects/blackwood-mansion/bat.png', audio: '../../asset/audio/bat.mp3'},
  { id: 13, name: 'Zombie', image: '/projects/blackwood-mansion/zombie.png', audio: '../../asset/audio/zombie.mp3'},
  { id: 14, name: 'Thick Fog', image: '/projects/blackwood-mansion/fog.png', audio: '../../asset/audio/fog.mp3'},
  { id: 15, name: 'Broken Clock',image: '/projects/blackwood-mansion/clock.png',  audio: '../../asset/audio/clock.mp3'},
];

const getCardSet = (type: CardSetType): Card[] => {
  switch (type) {
    case 'set1':
      return cardSet1;
    case 'set2':
      return cardSet2;
    case 'set3':
      return cardSet3;
    default:
      return cardSet1;
  }
};

export const CardProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [cardSetType, setCardSetType] = useState<CardSetType>('set1');
  const [clickOrder, setClickOrder] = useState<number[]>([]);

  const cardData = getCardSet(cardSetType);

  const setCardSet = (type: CardSetType) => {
    setCardSetType(type);
    setClickOrder([]);
  };

  const handleTempArr = (card: Card) => {
    if (clickOrder.length < cardData.length) {
      if (!clickOrder.includes(card.id)) {
        setClickOrder([...clickOrder, card.id]);
      } else {
        console.log(`Card ${card.id} already clicked.`);
      }
    }
  };

  const checkOrder = (array: number[]) => {
    const orders: Record<CardSetType, number[]> = {
      set1: [2, 5, 3, 1, 4],
      set2: [6, 10, 7, 8, 9],
      set3: [12, 15, 11, 13, 14],
    };
    const correctOrder = orders[cardSetType];
    const isCorrect = correctOrder.every((val, index) => val === array[index]);

    if (isCorrect) {
      alert('Order is correct');
    } else {
      alert('Order is NOT correct');
      setClickOrder([]);
    }
  };

  const getCardNumber = (cardId: number): number | null => {
    const index = clickOrder.indexOf(cardId);
    return index >= 0 ? index + 1 : null;
  };

  return (
    <CardContext.Provider
      value={{
        cardData,
        setCardSet,
        currentArray: clickOrder,
        handleTempArr,
        checkOrder,
        getCardNumber,
      }}
    >
      {children}
    </CardContext.Provider>
  );
};
