import React, { CSSProperties, useEffect, useRef, useState } from 'react';
import styled from 'styled-components';
import { TypeAnimation } from 'react-type-animation';
import Modal from 'react-modal';
import { Html5QrcodeScanner } from 'html5-qrcode';
import { useScreenVisibility } from '../ScreenVisibilityContext';
import Carousel from 'react-spring-3d-carousel';
import { config } from 'react-spring';
import { useCardContext } from '../CardContext';

Modal.setAppElement('#root');

const CardContainer = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
`;

interface QrReaderProps {
  delay: number;
  style: CSSProperties;
  onError: (err: any) => void;
  onScan: (data: string | null) => void;
}

const QrReader: React.FC<QrReaderProps> = ({ delay, style, onError, onScan }) => {
  const qrScannerRef = useRef<HTMLDivElement | null>(null);

  useEffect(() => {
    if (qrScannerRef.current) {
      const qrCodeScanner = new Html5QrcodeScanner(
        "qr-reader",
        { fps: 10, qrbox: 250 },
        false
      );

      qrCodeScanner.render(
        (decodedText: string) => onScan(decodedText),
        (error: string) => onError(error)
      );

      return () => {
        qrCodeScanner.clear();
      };
    }
  }, [onScan, onError]);

  return (
    <div style={style}>
      <div ref={qrScannerRef} id="qr-reader" style={{ width: '100%', height: '100%' }}></div>
    </div>
  );
};

const Chapter1: React.FC = () => {
  const [showModal, setShowModal] = useState(false);
  const [scanData, setScanData] = useState<string>('Not Found');
  const [jsonData, setJsonData] = useState<any>(null);
  const [placeholders, setPlaceholders] = useState([
    { id: 1, name: 'Placeholder 1', image: '/path/to/placeholder1.png' },
    { id: 2, name: 'Placeholder 2', image: '/path/to/placeholder2.png' },
    { id: 3, name: 'Placeholder 3', image: '/path/to/placeholder3.png' },
    { id: 4, name: 'Placeholder 4', image: '/path/to/placeholder4.png' },
  ]);

  const { screenVisibility, handleScreen } = useScreenVisibility();
  const {
    cardData,
    handleTempArr,
    checkOrder,
    getCardNumber,
    currentArray,
  } = useCardContext();

  const [goToSlide, setGoToSlide] = useState<number | undefined>(undefined);

  const openModal = () => setShowModal(true);
  const closeModal = () => setShowModal(false);
  const goBackToMain = () => handleScreen('main');
  const goToChapter2 = () => handleScreen('chapter_2');

  const handleCardClick = (cardIndex: number) => {
    handleTempArr(cardData[cardIndex]);
    setShowModal(true);
  };

  const carouselSlides = placeholders.map((card, index) => ({
    key: index,
    content: (
      <div
        onClick={() => handleCardClick(index)}
        className="flex flex-col items-center justify-center bg-white shadow-lg rounded p-4 cursor-pointer"
      >
        <img
          src={card.image}
          alt={card.name}
          style={{ width: '200px', height: '200px' }}
        />
        <span className="mt-2 text-xl">{getCardNumber(card.id)}</span>
        <p className="mt-1 text-gray-600">{card.name}</p>
      </div>
    ),
    onClick: () => setGoToSlide(index),
  }));

  const delay = (ms: number) => new Promise(res => setTimeout(res, ms));

  const handleScan = async (data: string | null) => {
    if (!data) return;
    setScanData(data);
    console.log('Scanned:', data);

    try {
      const response = await fetch(data);
      if (!response.ok) throw new Error('Fetch failed');
      const json = await response.json();
      setJsonData(json);

      // Wait 1 second before updating placeholders
      await delay(1000);

      if (Array.isArray(json)) {
        const updated = [...placeholders];

        json.forEach((item, i) => {
          const match = cardData.find(card => card.id === item.id);
          if (match && updated[i]) {
            updated[i] = {
              id: match.id,
              name: match.name,
              image: match.image,
            };
          }
        });

        setPlaceholders(updated);
      }

    } catch (err) {
      console.error('Error:', err);
      setJsonData({ error: 'Could not fetch' });
    }
  };

  return (
    <div>
      <h1>Welcome to Chapter 1!</h1>
      <TypeAnimation
        sequence={[
          "It was a dark, stormy evening when the call came in...",
          1000,
        ]}
        speed={50}
      />

      <button onClick={openModal} className="mt-4 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600">
        Open QR Code Modal
      </button>

      <Modal
        isOpen={showModal}
        onRequestClose={closeModal}
        contentLabel="QR Code Modal"
        className="modal-content"
        overlayClassName="modal-overlay"
        closeTimeoutMS={200}
      >
        <div className="modal-header">
          <h2>🪟 QR Code Scanner</h2>
          <button onClick={closeModal} className="close-btn">✕</button>
        </div>
        <div className="modal-body">
          <QrReader
            delay={300}
            style={{ width: '100%' }}
            onError={(err) => console.error(err)}
            onScan={handleScan}
          />

          <p>Scanned Data: {scanData}</p>
          {jsonData && (
            <div>
              <h3>Fetched JSON:</h3>
              <pre>{JSON.stringify(jsonData, null, 2)}</pre>
            </div>
          )}
        </div>
      </Modal>

      <div style={{ width: '500px', height: '400px', margin: '40px auto' }}>
        <Carousel
          slides={carouselSlides}
          goToSlide={goToSlide}
          offsetRadius={2}
          showNavigation={true}
          animationConfig={config.gentle}
        />
      </div>

      <button onClick={() => {
        if (currentArray.length === cardData.length) {
          checkOrder(currentArray);
        } else {
          alert('Please click all cards first.');
        }
      }}>
        Check Order
      </button>

      <button onClick={goBackToMain}>Back to Main</button>
      <button onClick={goToChapter2}>Go to Chapter 2</button>
    </div>
  );
};

export default Chapter1;
